     /*                                                                     */            /* RADD */
     /* =================================================================== */            /* RADD */
     /*  History:                                                           */            /* RADD */
     /*                                                                     */            /* RADD */
     /*  Datum       Name          nderung                                 */            /* RADD */
     /*  ----------  ------------  ---------------------------------------  */            /* RADD */
     /*  07.03.2014  Th.Raddatz    Added special value *DFT to parameters   */            /* RADD */
     /*                            SRCLIB, SRCFILE and SRCMBR.              */            /* RADD */
     /*                                                                     */            /* RADD */
     /* =================================================================== */            /* RADD */
     /* >>PRE-COMPILER<<                                                    */            /* RADD */
     /*                                                                     */            /* RADD */
     /*   >>CRTCMD<< CRTCLMOD     MODULE(&LI/&OB) +                         */            /* RADD */
     /*                           SRCFILE(&SL/&SF) +                        */            /* RADD */
     /*                           SRCMBR(&SM);                              */            /* RADD */
     /*                                                                     */            /* RADD */
     /*   >>COMPILE<<                                                       */            /* RADD */
     /*     >>PARM<< DBGVIEW(*LIST);                                        */            /* RADD */
     /*   >>END-COMPILE<<                                                   */            /* RADD */
     /*                                                                     */            /* RADD */
     /*   >>EXECUTE<<                                                       */            /* RADD */
     /*                                                                     */            /* RADD */
     /*   >>CMD<<    CRTPGM       PGM(&LI/STRPREPRC ) +                     */            /* RADD */
     /*                           MODULE(&LI/STRPREPRC1 +                   */            /* RADD */
     /*                                  &LI/STRPREPRC2 +                   */            /* RADD */
     /*                                  &LI/STRPREPRC3 ) +                 */            /* RADD */
     /*                           ENTMOD(&LI/STRPREPRC1) +                  */            /* RADD */
     /*                           ACTGRP(*NEW) +                            */            /* RADD */
     /*                           DETAIL(*BASIC);                           */            /* RADD */
     /*                                                                     */            /* RADD */
     /* >>END-PRE-COMPILER<<                                                */            /* RADD */
     /* =================================================================== */            /* RADD */
             PGM        PARM(&QUSESRCF   +                                                          
                             &USESRCMBR  +                                                          
                             &CMD        +                                                          
                             &CHGOBJD    +                                                          
                             &OBJTYPE    +                                                          
                             &OBJSRCFILE +                                                          
                             &OBJSRCLIB  +                                                          
                             &OBJSRCMBR  +                                                          
                             &XLIB       +                                                          
                             &XOBJ       +                                                          
                             &XTYPECODE  +                                                          
                             &XSRCLIB    +                                                          
                             &XSRCFILE   +                                                          
                             &XSRCMBR    +                                                          
                             &XTESTLIB   +                                                          
                             &XTESTOBJ   +                                                          
                             &XTGTRLS    +                                                          
                             &XFRMSRCLIB +                                                          
                             &XFRMSRCFIL +                                                          
                             &XFRMSRCMBR +                                                          
                             &XUSER0     +                                                          
                             &XUSER1     +                                                          
                             &XUSER2     +                                                          
                             &XUSER3     +                                                          
                             &XUSER4     +                                                          
                             &XUSER5     +                                                          
                             &XUSER6     +                                                          
                             &XUSER7     +                                                          
                             &XUSER8     +                                                          
                             &XUSER9     +                                                          
                             &XTAGSREQ   +                                                          
                             &XMSGF      )                                                /* RADD */
                                                                                          /* RADD */
             DCL        VAR(&QUSESRCF  ) TYPE(*CHAR) LEN(  20)                            /* RADD */
             DCL        VAR(&USESRCMBR ) TYPE(*CHAR) LEN(  10)                            /* RADD */
             DCL        VAR(&CMD       ) TYPE(*CHAR) LEN(2048)                            /* RADD */
             DCL        VAR(&CHGOBJD   ) TYPE(*CHAR) LEN(   4)                            /* RADD */
             DCL        VAR(&OBJTYPE   ) TYPE(*CHAR) LEN(   4)                            /* RADD */
             DCL        VAR(&OBJSRCFILE) TYPE(*CHAR) LEN(  10)                            /* RADD */
             DCL        VAR(&OBJSRCLIB ) TYPE(*CHAR) LEN(  10)                            /* RADD */
             DCL        VAR(&OBJSRCMBR ) TYPE(*CHAR) LEN(  10)                            /* RADD */
             DCL        VAR(&XLIB      ) TYPE(*CHAR) LEN(  10)                            /* RADD */
             DCL        VAR(&XOBJ      ) TYPE(*CHAR) LEN(  10)                            /* RADD */
             DCL        VAR(&XTYPECODE ) TYPE(*CHAR) LEN(   6)                            /* RADD */
             DCL        VAR(&XSRCLIB   ) TYPE(*CHAR) LEN(  10)                            /* RADD */
             DCL        VAR(&XSRCFILE  ) TYPE(*CHAR) LEN(  10)                            /* RADD */
             DCL        VAR(&XSRCMBR   ) TYPE(*CHAR) LEN(  10)                            /* RADD */
             DCL        VAR(&XTESTLIB  ) TYPE(*CHAR) LEN(  10)                            /* RADD */
             DCL        VAR(&XTESTOBJ  ) TYPE(*CHAR) LEN(  10)                            /* RADD */
             DCL        VAR(&XTGTRLS   ) TYPE(*CHAR) LEN(  10)                            /* RADD */
             DCL        VAR(&XFRMSRCLIB) TYPE(*CHAR) LEN(  10)                            /* RADD */
             DCL        VAR(&XFRMSRCFIL) TYPE(*CHAR) LEN(  10)                            /* RADD */
             DCL        VAR(&XFRMSRCMBR) TYPE(*CHAR) LEN(  10)                            /* RADD */
             DCL        VAR(&XUSER0    ) TYPE(*CHAR) LEN(  50)                            /* RADD */
             DCL        VAR(&XUSER1    ) TYPE(*CHAR) LEN(  50)                            /* RADD */
             DCL        VAR(&XUSER2    ) TYPE(*CHAR) LEN(  50)                            /* RADD */
             DCL        VAR(&XUSER3    ) TYPE(*CHAR) LEN(  50)                            /* RADD */
             DCL        VAR(&XUSER4    ) TYPE(*CHAR) LEN(  50)                            /* RADD */
             DCL        VAR(&XUSER5    ) TYPE(*CHAR) LEN(  50)                            /* RADD */
             DCL        VAR(&XUSER6    ) TYPE(*CHAR) LEN(  50)                            /* RADD */
             DCL        VAR(&XUSER7    ) TYPE(*CHAR) LEN(  50)                            /* RADD */
             DCL        VAR(&XUSER8    ) TYPE(*CHAR) LEN(  50)                            /* RADD */
             DCL        VAR(&XUSER9    ) TYPE(*CHAR) LEN(  50)                            /* RADD */
             DCL        VAR(&XTAGSREQ  ) TYPE(*LGL ) LEN(   1)                            /* RADD */
             DCL        VAR(&XMSGF     ) TYPE(*CHAR) LEN(  10)                            /* RADD */
                                                                                          /* RADD */
             DCL        VAR(&USESRCFILE) TYPE(*CHAR) LEN(  10)                            /* RADD */
             DCL        VAR(&USESRCLIB ) TYPE(*CHAR) LEN(  10)                            /* RADD */
                                                                                          /* RADD */
/* Fehlerbehandlung     */                                                                /* RADD */
             DCL        VAR(&ERROR    )  TYPE(*LGL) +                                               
                          VALUE('0')                                                      /* RADD */
             DCL        VAR(&TRUE     )  TYPE(*LGL) +                                               
                          VALUE('1')                                                      /* RADD */
             DCL        VAR(&FALSE    )  TYPE(*LGL) +                                               
                          VALUE('0')                                                      /* RADD */
             DCL        VAR(&ERRMSGKEY)  TYPE(*CHAR) LEN(4)                               /* RADD */
                                                                                          /* RADD */
             DCL        VAR(&ERRMSGTYP)  TYPE(*CHAR) LEN(10) +                                      
                          VALUE('*DIAG     ')                                                       
             DCL        VAR(&ERRNBRTYP)  TYPE(*CHAR) LEN(4) +                                       
                          VALUE(X'00000001')                                              /* RADD */
             DCL        VAR(&ERRPGMMSGQ) TYPE(*CHAR) LEN(10) +                                      
                          VALUE('*PGMBDY')                                                /* RADD */
             DCL        VAR(&ERRSTKCTR)  TYPE(*CHAR) LEN(4) +                                       
                          VALUE(X'00000001')                                              /* RADD */
             DCL        VAR(&ERRCODE  )  TYPE(*CHAR) LEN(4) +                                       
                          VALUE(X'00000000')                                              /* RADD */
                                                                                          /* RADD */
/* Sende alle Fehler zur Error Handling Routine */                                        /* RADD */
             MONMSG     MSGID(CPF0000) EXEC(GOTO CMDLBL(ERROR))                           /* RADD */
                                                                                          /* RADD */
             CHGVAR     VAR(&ERROR    ) VALUE(&FALSE)                                     /* RADD */
                                                                                          /* RADD */
 /* -------------------------------------------------------------- */                     /* RADD */
 /*  Los geht's                                                    */                     /* RADD */
 /* -------------------------------------------------------------- */                     /* RADD */
                                                                                          /* RADD */
             CHGVAR     VAR(&USESRCFILE) VALUE(%SST(&QUSESRCF  1 10))                     /* RADD */
             CHGVAR     VAR(&USESRCLIB ) VALUE(%SST(&QUSESRCF 11 10))                     /* RADD */
                                                                                          /* RADD */
 /* - - - - - - - - - - - - - - - - - - */                                                /* RADD */
 /*  Prfen der Quelldatei/-teildatei   */                                                /* RADD */
 /* - - - - - - - - - - - - - - - - - - */                                                /* RADD */
             CHKOBJ     OBJ(&USESRCLIB) OBJTYPE(*LIB)                                     /* RADD */
             CHKOBJ     OBJ(&USESRCLIB/&USESRCFILE) OBJTYPE(*FILE)                        /* RADD */
             CHKOBJ     OBJ(&USESRCLIB/&USESRCFILE) OBJTYPE(*FILE) +                                
                          MBR(&USESRCMBR)                                                 /* RADD */
                                                                                          /* RADD */
 /* - - - - - - - - - - - - - - - - - - */                                                /* RADD */
 /*  Prfen der Steuerdatei STRPRE1     */                                                /* RADD */
 /* - - - - - - - - - - - - - - - - - - */                                                /* RADD */
             CHKOBJ     OBJ(*LIBL/STRPRE1) OBJTYPE(*FILE)                                 /* RADD */
             CHKOBJ     OBJ(*LIBL/STRPRE1) OBJTYPE(*FILE) +                                         
                          MBR(*FIRST)                                                     /* RADD */
             CALLPRC    PRC(P_INITPREPRZ)                                                 /* RADD */
                                                                                          /* RADD */
 /* - - - - - - - - - - - - - - - - - - */                                                /* RADD */
 /*  Prfen Befehl CHGOBJDA             */                                                /* RADD */
 /* - - - - - - - - - - - - - - - - - - */                                                /* RADD */
             IF         COND(&CHGOBJD *EQ '*YES') THEN(DO)                                /* RADD */
               CHKOBJ     OBJ(*LIBL/CHGOBJDA) OBJTYPE(*CMD)                               /* RADD */
               MONMSG     MSGID(CPF9801) EXEC(DO)                                         /* RADD */
                 SNDPGMMSG  MSGID(PRZ0021) MSGF(*LIBL/&XMSGF) +                                     
                            TOPGMQ(*PRV (*PGMBDY)) MSGTYPE(*DIAG)                         /* RADD */
                 SNDPGMMSG  MSGID(PRZ0022) MSGF(*LIBL/&XMSGF) +                                     
                            TOPGMQ(*PRV (*PGMBDY)) MSGTYPE(*ESCAPE)                       /* RADD */
               ENDDO                                                                      /* RADD */
             ENDDO                                                                        /* RADD */
                                                                                          /* RADD */
 /* - - - - - - - - - - - - - - - - - - */                                                /* RADD */
 /*  Starten Pre-Prozessor              */                                                /* RADD */
 /* - - - - - - - - - - - - - - - - - - */                                                /* RADD */
             OVRDBF     FILE(SRCFILE)                     +                                         
                          TOFILE(&USESRCLIB/&USESRCFILE)  +                                         
                          MBR(&USESRCMBR)                 +                                         
                          OVRSCOPE(*CALLLVL)                                              /* RADD */
                                                                                                    
             IF         COND(&XSRCLIB *EQ '*DFT') THEN(DO)                                          
                CHGVAR     VAR(&XSRCLIB) VALUE(&USESRCLIB)                                          
             ENDDO                                                                                  
                                                                                                    
             IF         COND(&XSRCFILE *EQ '*DFT') THEN(DO)                                         
                CHGVAR     VAR(&XSRCFILE) VALUE(&USESRCFILE)                                        
             ENDDO                                                                                  
                                                                                                    
             IF         COND(&XSRCMBR *EQ '*DFT') THEN(DO)                                          
                CHGVAR     VAR(&XSRCMBR) VALUE(&USESRCMBR)                                          
             ENDDO                                                                                  
                                                                                                    
             IF         COND(&OBJSRCFILE *EQ '*DFT') THEN(DO)                                       
                CHGVAR     VAR(&OBJSRCFILE) VALUE(&XSRCFILE)                                        
             ENDDO                                                                                  
                                                                                                    
             IF         COND(&OBJSRCLIB *EQ '*DFT') THEN(DO)                                        
                CHGVAR     VAR(&OBJSRCLIB) VALUE(&XSRCLIB)                                          
             ENDDO                                                                                  
                                                                                                    
             IF         COND(&OBJSRCMBR *EQ '*DFT') THEN(DO)                                        
                CHGVAR     VAR(&OBJSRCMBR) VALUE(&XSRCMBR)                                          
             ENDDO                                                                                  
                                                                                                    
             IF         COND(&XOBJ *EQ '*DFT') THEN(DO)                                             
                CHGVAR     VAR(&XOBJ) VALUE(&XSRCMBR)                                               
             ENDDO                                                                                  
                                                                                                    
             CALLPRC    PRC(P_STRPREPRC) PARM(&USESRCFILE +                                         
                                              &USESRCLIB  +                                         
                                              &USESRCMBR  +                                         
                                              &CMD        +                                         
                                              &CHGOBJD    +                                         
                                              &OBJTYPE    +                                         
                                              &OBJSRCFILE +                                         
                                              &OBJSRCLIB  +                                         
                                              &OBJSRCMBR  +                                         
                                              &XLIB       +                                         
                                              &XOBJ       +                                         
                                              &XTYPECODE  +                                         
                                              &XSRCLIB    +                                         
                                              &XSRCFILE   +                                         
                                              &XSRCMBR    +                                         
                                              &XTESTLIB   +                                         
                                              &XTESTOBJ   +                                         
                                              &XTGTRLS    +                                         
                                              &XFRMSRCLIB +                                         
                                              &XFRMSRCFIL +                                         
                                              &XFRMSRCMBR +                                         
                                              &XUSER0     +                                         
                                              &XUSER1     +                                         
                                              &XUSER2     +                                         
                                              &XUSER3     +                                         
                                              &XUSER4     +                                         
                                              &XUSER5     +                                         
                                              &XUSER6     +                                         
                                              &XUSER7     +                                         
                                              &XUSER8     +                                         
                                              &XUSER9     +                                         
                                              &XTAGSREQ   +                                         
                                              &XMSGF      )                               /* RADD */
                                                                                          /* RADD */
             DLTOVR     FILE(SRCFILE) LVL(*)                                              /* RADD */
                                                                                          /* RADD */
 /* -------------------------------------------------------------- */                     /* RADD */
 /*  Programm beenden                                              */                     /* RADD */
 /* -------------------------------------------------------------- */                     /* RADD */
                                                                                          /* RADD */
ENDE:                                                                                     /* RADD */
                                                                                          /* RADD */
/* -------------------------------------------------------------- */                      /* RADD */
/*    Fehler aufgetreten ?                                        */                      /* RADD */
/* -------------------------------------------------------------- */                      /* RADD */
                                                                                          /* RADD */
             IF         COND(&ERROR) THEN(DO)                                             /* RADD */
               /* Sende letzte *ESCAPE nochmals   */                                      /* RADD */
               CALL       PGM(QMHRSNEM) PARM(&ERRMSGKEY &ERRCODE)                         /* RADD */
               MONMSG     MSGID(CPF0000)                                                  /* RADD */
             ENDDO                                                                        /* RADD */
                                                                                          /* RADD */
             RETURN                                                                       /* RADD */
                                                                                          /* RADD */
/* ---------------- */                                                                    /* RADD */
/* Nachrichten      */                                                                    /* RADD */
/* weiterreichen    */                                                                    /* RADD */
/* ---------------- */                                                                    /* RADD */
                                                                                          /* RADD */
 ERROR:                                                                                   /* RADD */
             IF         COND(&ERROR) THEN(GOTO CMDLBL(ENDE))                              /* RADD */
             ELSE       CHGVAR     VAR(&ERROR) VALUE(&TRUE)                               /* RADD */
                                                                                          /* RADD */
/* Verschiebe alle *DIAG Nachrichten zur             */                                   /* RADD */
/* vorangegangenen Programmwarteschlange             */                                   /* RADD */
             CALL       PGM(QMHMOVPM) PARM(&ERRMSGKEY &ERRMSGTYP +                                  
                          &ERRNBRTYP &ERRPGMMSGQ &ERRSTKCTR &ERRCODE)                     /* RADD */
                                                                                          /* RADD */
             GOTO       CMDLBL(ENDE)                                                      /* RADD */
                                                                                          /* RADD */
             ENDPGM                                                                       /* RADD */
