                ) +                                                                                 
                SEV(40) +                                                                           
                FMT( +                                                                              
                    (*CHAR 030) +                                                                   
                    (*CHAR 030) +                                                                   
                ) +                                                                                 
                ALROPT(*NO) +                                                                       
                CCSID( 65535) +                                                                     
                /*  -EOF-  */                                                                       
                                                                                                    
