// Befehl-Anweisung >>LIBL<< if (mode = cMODE_LIBL); // zufgen Schlsselwortdaten lineData.keyData = addData(cTAG_LIBL : lineData.keyData : srcDta : lineData.continue);
// prfen auf Fortsetzungszeile if (lineData.continue <> cEND_OF_LINE); iter; else; cmdString = 'CHGLIBL + LIBL(' + lineData.keyData + ')'; performSetLibraryList( replaceVars(cmdString: variables): variables); resetLineData(lineData); mode = popMode(); iter; endif; endif;
// Befehl-Anweisung >>CURLIB<< if (mode = cMODE_CURLIB); // zufgen Schlsselwortdaten lineData.keyData = addData(cTAG_CURLIB : lineData.keyData : srcDta : lineData.continue);
// prfen auf Fortsetzungszeile if (lineData.continue <> cEND_OF_LINE); iter; else; cmdString = 'CHGLIBL + CURLIB(' + lineData.keyData + ')'; performSetCurrentLibrary( replaceVars(cmdString: variables): variables); resetLineData(lineData); mode = popMode(); iter; endif; endif;
enddo;
close SRCFILE; close STRPRE1;
// Ausfhren >>EXECUTE<< implizit, weil // kein Pre-Prozessor Abschnitt gefunden werden konnte isCmdMissingError = cFalse; if (execCmdImplizit = cTrue); if (cmd = ''); isCmdMissingError = cTrue; else;
if (i_useLibl); // Set library list if (variables.libl.usrLibl.numE >= 1); cmdString = 'CHGLIBL LIBL(&LL)'; performSetLibraryList( replaceVars(cmdString: variables): variables); endif;
// Set current library if (variables.libl.curLib <> cNone); cmdString = 'CHGLIBL CURLIB(&CL)'; performSetCurrentLibrary( replaceVars(cmdString: variables): variables); endif; endif;
// Execute command cmdString = cmd; performExecute(replaceVars(cmdString: variables): cmdChgObjD); endif; endif;
// Close event file, if left open. if (g_eventf.isOpen); closeEventFile(); g_eventf.isOpen = cFalse; endif;
// Restore library list to startup value restoreLibraryList();
ceeutx(%paddr('abnormalEndHandler'): *omit);
// Senden Abbruchnachricht, weil keine STRPREPRC Anweisungen // gefunden worden sind if (not foundPrePrcTags and i_tagsReq); clear PRZ0024; PRZ00241 = f1.file; PRZ00242 = f1.lib; PRZ00243 = f1.mbr; sendMsg(f_newMsg('PRZ0024': PRZ0024: cTYPE_ESCAPE)); endif;
if (isCmdMissingError); clear PRZ0028; PRZ00281 = f1.file; PRZ00282 = f1.lib; PRZ00283 = f1.mbr; sendMsg(f_newMsg('PRZ0028': PRZ0028: cTYPE_ESCAPE)); endif;
/end-free P E
* ============================================================= * Abnormal end handler. * ============================================================= P abnormalEndHandler... P b D pi D i_pRtxToken * const /free
// Restore library list to startup value restoreLibraryList();
/end-free P E
* ============================================================= * Restores the library list to what it was at the * start of the STRPREPRC utility. * ============================================================= P restoreLibraryList... P b D pi * D currentLiblList... D ds likeds(liblList_t) inz D liblStr s like(cmdLong_t) D cmdString s like(cmdLong_t) inz /free
currentLiblList = retrieveLibraryList(); if (g_startUpLiblList <> currentLiblList); liblStr = libListToStr(g_startUpLiblList.usrLibl); cmdString = 'CHGLIBL + LIBL(' + liblStr + ') + CURLIB(' + g_startUpLiblList.curLib + ')'; executeCmd(cFalse: cmdString); appendToJobLog('Library list restored to:'); appendToJobLog(liblStr); endif;
/end-free P E
* ============================================================= * Reset. * ============================================================= P resetLineData... P b D pi D io_lineData likeds(lineData_t) /free
io_lineData.continue = cNEW_LINE; io_lineData.keyData = '';
/end-free P E
* ============================================================= * Replaces the replacement variables. * ============================================================= P replaceVars... P b D pi like(cmdLong_t) D i_cmd value like(cmdLong_t) D i_variables value likeds(variables_t) * D cmdString s like(i_cmd) D posOfVar s like(int4_t) inz D i s 5i 0 * * ... Fehlender Wert fr Variable an Position &1 des auszufhrenden Befehls. /COPY QSTRPREPRC,PRZ0023 /free
cmdString = i_cmd;
// -------------------------------- // Replace user-defined variables // --------------------------------
for i = 1 to i_variables.usrDefVars.numE; replaceVar(i_variables.usrDefVars.var(i).name : i_variables.usrDefVars.var(i).value: cmdString); endfor;
// -------------------------------- // Replace STRPREPRC variables // --------------------------------
// Ersetzen Lib (&LI) if (i_variables.lib <> cNone); replaceVar('&LI': i_variables.lib: cmdString); endif;
// Ersetzen Objekt (&OB) if (i_variables.obj <> cNone); replaceVar('&OB': i_variables.obj: cmdString); endif;
// Ersetzen Type Code (&TY) if (i_variables.typeCode <> cNone); replaceVar('&TY': i_variables.typeCode: cmdString); endif;
// Ersetzen Source Lib (&SL) if (i_variables.srcLib <> cNone); replaceVar('&SL': i_variables.srcLib: cmdString); endif;
// Ersetzen Source File (&SF) if (i_variables.srcFile <> cNone); replaceVar('&SF': i_variables.srcFile: cmdString); endif;
// Ersetzen Source Mbr (&SM) if (i_variables.srcMbr <> cNone); replaceVar('&SM': i_variables.srcMbr: cmdString); endif;
// Ersetzen Test Lib (&TL) if (i_variables.testLib <> cNone); replaceVar('&TL': i_variables.testLib: cmdString); endif;
// Ersetzen Test Object (&TO) if (i_variables.testObj <> cNone); replaceVar('&TO': i_variables.testObj: cmdString); endif;
// Ersetzen Taget Rel. (&TO) if (i_variables.tgtRls <> cNone); replaceVar('&TR': i_variables.tgtRls: cmdString); endif;
// Ersetzen From Src Lib (&SL) if (i_variables.frmSrcLib <> cNone); replaceVar('&FL': i_variables.frmSrcLib: cmdString); endif;
// Ersetzen From Src File (&SF) if (i_variables.frmSrcFile <> cNone); replaceVar('&FF': i_variables.frmSrcFile: cmdString); endif;
// Ersetzen From Src Mbr (&SM) if (i_variables.frmSrcMbr <> cNone); replaceVar('&FM': i_variables.frmSrcMbr: cmdString); endif;
// Ersetzen Parameter (&USER0) if (i_variables.user0 <> cNone); replaceVar('&U0': i_variables.user0: cmdString); endif;
// Ersetzen Parameter (&USER1) if (i_variables.user1 <> cNone); replaceVar('&U1': i_variables.user1: cmdString); endif;
// Ersetzen Parameter (&USER2) if (i_variables.user2 <> cNone); replaceVar('&U2': i_variables.user2: cmdString); endif;
// Ersetzen Parameter (&USER3) if (i_variables.user3 <> cNone); replaceVar('&U3': i_variables.user3: cmdString); endif;
// Ersetzen Parameter (&USER4) if (i_variables.user4 <> cNone); replaceVar('&U4': i_variables.user4: cmdString); endif;
// Ersetzen Parameter (&USER5) if (i_variables.user5 <> cNone); replaceVar('&U5': i_variables.user5: cmdString); endif;
// Ersetzen Parameter (&USER6) if (i_variables.user6 <> cNone); replaceVar('&U6': i_variables.user6: cmdString); endif;
// Ersetzen Parameter (&USER7) if (i_variables.user7 <> cNone); replaceVar('&U7': i_variables.user7: cmdString); endif;
// Ersetzen Parameter (&USER8) if (i_variables.user8 <> cNone); replaceVar('&U8': i_variables.user8: cmdString); endif;
// Ersetzen Parameter (&USER9) if (i_variables.user9 <> cNone); replaceVar('&U9': i_variables.user9: cmdString); endif;
// Ersetzen Parameter (&LL) if (i_variables.libl.usrLibl.numE >= 1); replaceVar( '&LL': libListToStr(i_variables.libl.usrLibl): cmdString); endif;
// Ersetzen Parameter (&LX) if (i_variables.prvLibl.usrLibl.numE >= 1); replaceVar( '&LX': libListToStr(i_variables.prvLibl.usrLibl): cmdString); endif;
// Ersetzen Parameter Sonderwert (*LX) // if (i_variables.prvLibl.usrLibl.numE >= 1); // replaceVar( // '*LX': libListToStr(i_variables.prvLibl.usrLibl): cmdString); // endif;
// Ersetzen Parameter (&CL) if (i_variables.libl.curLib <> cNone); replaceVar('&CL': i_variables.libl.curLib: cmdString); endif;
// Ersetzen Parameter (&CX) if (i_variables.prvLibl.curLib <> ''); replaceVar('&CX': i_variables.prvLibl.curLib: cmdString); endif;
// Ersetzen Parameter Sonderwert (*CX) // if (i_variables.prvLibl.curLib <> ''); // replaceVar('*CX': i_variables.prvLibl.curLib: cmdString); // endif;
// Prfen, ob alle Variablen ersetzt worden sind posOfVar = %scan('&': cmdString);
if (posOfVar <> 0); clear PRZ0023; PRZ00231 = posOfVar; PRZ00232 = cmdString; sendMsg(f_newMsg('PRZ0023': PRZ0023: cTYPE_DIAG)); endAbnormal(); endif;
return cmdString;
/end-free P E
* ============================================================= * Converts a library list to string. * ============================================================= P libListToStr... P b D pi like(cmdLong_t) D i_libl value likeds(libl_t) * D i s 10i 0 D liblStr s like(cmdLong_t) /free
for i = 1 to i_libl.numE; if (liblStr <> ''); liblStr = liblStr + ' '; endif; liblStr = liblStr + %trim(i_libl.name(i)); endfor;
return liblStr;
/end-free p e
* ============================================================= * Execute >>EXECUTE<< tag. (Execute main creation command) * ============================================================= P performExecute... P b D pi D i_cmd value like(cmdLong_t) D i_cmdChgObjD value like(cmdLong_t) * * ... EVFEVENT Datei konnte nicht zur Ausgabe vorbereitet werden. /COPY QSTRPREPRC,PRZ0026 /free
if (g_option.isEventf); monitor; prepareEventFile(); on-error; clear PRZ0026; sendMsg(f_newMsg('PRZ0026': PRZ0026: cTYPE_ESCAPE)); endAbnormal(); endmon; endif;
if (not validateCmd(i_cmd)); endAbnormal(); endif;
if (not executeCmd(cTrue: i_cmd)); if (%scan(cEVENTF_YES: i_cmd) > 0); endAbnormal(retrieveEventFile(i_cmd)); // EVENT file created by IBM command else; endAbnormal(); endif; else; // Write event file, but change messages greater or // equal to "error" to "warning". if (%scan(cEVENTF_YES: i_cmd) > 0); endOK(retrieveEventFile(i_cmd)); // EVENT file created by IBM command else; endOK(); endif; endif;
if (i_cmdChgObjD <> ''); performCmd(i_cmdChgObjD); endif;
/end-free p e
* ============================================================= * Execute >>CMD<< tag (Pre and post commands) * ============================================================= P performCmd... P b D pi D i_cmd value like(cmdLong_t) * * ... EVFEVENT Datei konnte nicht zur Ausgabe vorbereitet werden. /COPY QSTRPREPRC,PRZ0026 /free
if (g_option.isEventf); monitor; prepareEventFile(); on-error; clear PRZ0026; sendMsg(f_newMsg('PRZ0026': PRZ0026: cTYPE_ESCAPE)); endAbnormal(); endmon; endif;
if (not validateCmd(i_cmd)); endAbnormal(); endif;
if (not executeCmd(cTrue: i_cmd)); if (%scan(cEVENTF_YES: i_cmd) > 0); endAbnormal(retrieveEventFile(i_cmd)); // EVENT file created by command else; endAbnormal(); endif; endif;
/end-free P e
* ============================================================= * Execute >>LIBL<< tag (set library list) * ============================================================= P performSetLibraryList... P b D pi D i_cmd value like(cmdLong_t) D io_variables likeds(variables_t) * D liblList ds likeds(liblList_t) inz * * ... EVFEVENT Datei konnte nicht zur Ausgabe vorbereitet werden. /COPY QSTRPREPRC,PRZ0026 /free
if (g_option.isEventf); monitor; prepareEventFile(); on-error; clear PRZ0026; sendMsg(f_newMsg('PRZ0026': PRZ0026: cTYPE_ESCAPE)); endAbnormal(); endmon; endif;
liblList = retrieveLibraryList(); io_variables.prvLibl.usrLibl = liblList.usrLibl;
// Change library list if (not validateCmd(i_cmd)); endAbnormal(); endif;
if (not executeCmd(cTrue: i_cmd)); if (%scan(cEVENTF_YES: i_cmd) > 0); endAbnormal(retrieveEventFile(i_cmd)); // EVENT file created by command else; endAbnormal(); endif; endif;
/end-free P e
* ============================================================= * Execute >>CURLIB<< tag (set current library) * ============================================================= P performSetCurrentLibrary... P b D pi D i_cmd value like(cmdLong_t) D io_variables likeds(variables_t) * D liblList ds likeds(liblList_t) inz * * ... EVFEVENT Datei konnte nicht zur Ausgabe vorbereitet werden. /COPY QSTRPREPRC,PRZ0026 /free
if (g_option.isEventf); monitor; prepareEventFile(); on-error; clear PRZ0026; sendMsg(f_newMsg('PRZ0026': PRZ0026: cTYPE_ESCAPE)); endAbnormal(); endmon; endif;
