// Change library list if (not validateCmd(i_cmd)); endAbnormal(); endif;
if (not executeCmd(cTrue: i_cmd)); if (%scan(cEVENTF_YES: i_cmd) > 0); endAbnormal(retrieveEventFile(i_cmd)); // EVENT file created by command else; endAbnormal(); endif; endif;
/end-free P e
* ============================================================= * Retrieves the current library list of the job. * ============================================================= P retrieveLibraryList... P b D pi likeds(liblList_t) * * Return value D libl ds likeds(liblList_t) inz * D rtvc0200 ds likeds(rtvc0200_t) based(pRtvc0200) D rcvVar s 4096a inz D errCode ds likeds(errCode_t) inz D i s 10i 0 D keysToRtn s 10i 0 dim(2) D pOffs s * inz D curLib s like(QWCRTVCA_libName_t) D based(pCurLib) D usrLibl s like(QWCRTVCA_libName_t) D dim(DIM_LIBLIST) based(pUsrLibl) /free
pRtvc0200 = %addr(rcvVar);
keysToRtn(1) = KEY_CURRENT_LIBRARY; keysToRtn(2) = KEY_USER_LIBRARIES; errCode = f_newApiErrCode(cFalse); // send *ESCAPE message on error QWCRTVCA(rcvVar: %size(rcvVar): 'RTVC0200' : %elem(keysToRtn): keysToRtn: errCode);
pOffs = %addr(rtvc0200) + %size(rtvc0200);
if (rtvc0200.numSysLiblE >= 1); // pSysLibl = pOffs; pOffs = pOffs + (rtvc0200.numSysLiblE * %size(QWCRTVCA_libName_t)); endif;
if (rtvc0200.numPrdLiblE >= 1); // pPrdLibl = pOffs; pOffs = pOffs + (rtvc0200.numPrdLiblE * %size(QWCRTVCA_libName_t)); endif;
if (rtvc0200.curLibExists >= CURLIB_EXISTS); pCurLib = pOffs; libl.curLib = curLib; pOffs = pOffs + %size(QWCRTVCA_libName_t); else; libl.curLib = CURLIB_CRTDFT; endif;
if (rtvc0200.numUsrLiblE >= 1); pUsrLibl = pOffs; for i = 1 to rtvc0200.numUsrLiblE; libl.usrLibl.numE += 1; libl.usrLibl.name(libl.usrLibl.numE) = usrLibl(i); endfor; pOffs = pOffs + (rtvc0200.numUsrLiblE * %size(QWCRTVCA_libName_t)); else; clear libl.usrLibl; endif;
return libl;
/end-free P e
* ============================================================= * Retrieve the name of the event file (library & member) * from the object creation command. * ============================================================= P retrieveEventFile... P b D pi likeds(qObj_t) D i_cmd value like(cmdLong_t) * * Return value D qObj ds likeds(qObj_t) inz * D cmd s like(i_cmd) D clCmd s 10a varying D clKwd s 10a varying D i s 10i 0 D s s 10i 0 D e s 10i 0 D parmValue s 30a varying * D SPACE c ' ' /free
clear qObj;
cmd = formatCmd(i_cmd); i = %scan(SPACE: cmd); if (i <= 1); return qObj; endif;
// Find command in command table and // get the keyword that specifies the // qualified object name. clKwd = ''; clCmd = %subst(cmd: 1: i-1); for i = 1 to %elem(g_crtCmds); if (g_crtCmds(i).cmd = clCmd); clKwd = %trim(g_crtCmds(i).keyword); leave; endif; endfor;
// If not found in command table or not present in // the command, retrieve the first keyword, assuming // that it is the keyword that specifies the // qualified object name. if (clKwd = '' or %scan(' ' + clKwd + '(': cmd) = 0); i = %scan(SPACE: cmd: 1); // Skip CL command s = %check(SPACE: cmd: i); // Skip spaces = Start of 1. keyword e = %scan('(': cmd: s); // Start of keyword value if ((e - s) <= 0); return qObj; endif; clKwd = %subst(cmd: s: e-s); endif;
s = %scan(clKwd + '(': cmd); // Start of object parameter e = %scan(')': cmd); // End of object parameter
s = s + %len(clKwd) + 1; if ((e - s) <= 0); return qObj; endif;
parmValue = %trim(%subst(cmd: s: e-s)); s = %scan('/': parmValue); if ((s - 1) > 0); if (s >= %len(parmValue)); return qObj; endif; qObj.lib = %subst(parmValue: 1: s-1); qObj.name = %subst(parmValue: s+1); else; qObj.lib = '*LIBL'; qObj.name = parmValue; endif;
return qObj;
/end-free P e
* ============================================================= * Zusammenbauen Schlsselwortdaten * ============================================================= P addData... P b D pi like(keyData_t) D i_tag value like(tag_t) D i_keyData value like(keyData_t) D i_srcDta value like(srcDta_t) D io_continue like(continue_t) * * Funktionswert D keyData s like(keyData_t) inz * * Nachrichten * ... Ungltiges Fortsetzungszeichen &1 in Zeile &2 gefunden. /COPY QSTRPREPRC,PRZ0005 * ... Das Zeilenendekennzeichen konnte nicht gefunden werden. /COPY QSTRPREPRC,PRZ0008 * ... Unbekannter Wert "&1" fr Fortsetzungszeichen gefunden. /COPY QSTRPREPRC,PRZ0009 * * Arbeitsfelder D start s like(int4_t) inz D static D dataLen s like(int4_t) inz D x s like(int4_t) inz /free
// ermitteln der getStartPosition if (io_continue = cNEW_LINE); start = getStartPos(i_tag: i_srcDta); endif;
// ermitteln Zeilenende x = getSrcDataLength(); dow (x >= start);
// ... Zeilenende if (%subst(i_srcDta: x: %len(io_continue)) = cEND_OF_LINE); leave; endif;
// ... '+' Zeichen if (%subst(i_srcDta: x: %len(io_continue)) = cTRUNC_CONT); leave; endif;
// ... '-' Zeichen if (%subst(i_srcDta: x: %len(io_continue)) = cCONT); leave; endif;
x = x - 1; enddo;
// ermitteln Datenlnge Fortsetzungszeichen dataLen = x - start;
// prfen Datenlnge if (dataLen < 0); clear PRZ0008; PRZ00081 = f1_line_number; sendMsg(f_newMsg('PRZ0008': PRZ0008: cTYPE_DIAG)); endAbnormal(); endif;
// Wenn eine Datenlnge ermittelt werden konnte, ... if (dataLen > 0); // ... dann // zufgen neue Daten zu Schlsselwort bei ... select; when (io_continue = cNEW_LINE); // ... neue Zeile keyData = %subst(i_srcDta: start: dataLen);
when (io_continue = cTRUNC_CONT); // ... Fortsetzungszeichen '+' keyData = i_keyData + %triml(%subst(i_srcDta: start:dataLen));
when (io_continue = cCONT); // ... Fortsetzungszeichen '-' keyData = i_keyData + %subst(i_srcDta: start: dataLen);
other; // ... dieser Fall kann nicht eintreten // weil ber die Prfung von dataLen sichergestellt ist, // dass immer ein gltiges Fortsetzungszeichen vorhanden ist. // Dennoch: clear PRZ0009; PRZ00091 = io_continue; sendMsg(f_newMsg('PRZ0009': PRZ0009: cTYPE_DIAG)); endAbnormal(); endsl;
else; // ... dann // beibehalten der bisherigen Daten keyData = i_keyData; endif;
// ermitteln neues Fortsetzungszeichen io_continue = %subst(i_srcdta: x: %len(io_continue));
// prfen neues Fortsetzungszeichen if (io_continue <> cEND_OF_LINE) and (io_continue <> cTRUNC_CONT) and (io_continue <> cCONT); clear PRZ0005; PRZ00051 = io_continue; PRZ00052 = f1_line_number; sendMsg(f_newMsg('PRZ0005': PRZ0005: cTYPE_DIAG)); endAbnormal(); endif;
return keyData;
/end-free P e * * ============================================================= * Ermitteln der Breite der Quellenangaben am >>WIDTH<< Tag * ============================================================= P getWidthValue... P b D pi 10i 0 D i_srcDta value like(srcDta_t) D i_length 10i 0 value * * Funktionswert D width s 10i 0 inz(80) * D cWIDTH_LEN c %len(width) D cWIDTH_DECPOS c %decpos(width) * * Arbeitsfelder D x s like(int4_t) inz D tmpWidth s 16a varying inz * * Konstanten D cNUMBERS c '0123456789' /free
// ermitteln der getStartPosition x = %scan(cTAG_WIDTH: %subst(i_srcDta: 1: i_length)); if (x = 0); return width; endif;
// berspringen von Leerzeichen zwischen >>WIDTH<< und der angegebenen Breite x = x + %len(cTAG_WIDTH); x = %check(' ': %subst(i_srcDta: 1: i_length): x);
dow (x >= 1 and x < i_length and %check(cNUMBERS: %subst(i_srcDta:x:1)) = 0); tmpWidth = tmpWidth + %subst(i_srcDta:x:1); x = x + 1; enddo;
if (tmpWidth <> ''); width = %dec(tmpWidth: cWIDTH_LEN: cWIDTH_DECPOS); endif;
return width;
/end-free P e * * ============================================================= * Ersetzen/Hinzufgen Schlsselwort * ============================================================= P replaceCmdKeyWord... P b D pi like(cmdLong_t) D i_cmd value like(cmdLong_t) D i_keyData value like(keyData_t) * * Funktionswert D cmd s like(cmdLong_t) inz * * Nachrichten * ... Das Ende des Schlsselwortes &1 konnte nicht gefunden werden. /COPY QSTRPREPRC,PRZ0006 * * Arbeitsfelder D startPos s like(int4_t) inz D dataLen s like(int4_t) inz D x s like(int4_t) inz D y s like(int4_t) inz D token s 11a varying inz D cmdDtaLen s like(int4_t) inz D numBracket s like(int4_t) inz /free
// ermitteln Befehlstoken x = %scan('(': i_keyData: 1); token = %subst(i_keyData: 1: x);
// ermitteln Ende des Befehlswortes ... // ... berspringen fhrende Leerzeichen x = %check(' ': i_cmd); // ... suchen Leerzeichen nach Befehlswort x = %scan(' ': i_cmd: x);
// suchen Token im vorhandenen Befehlstring dow (startPos < %len(i_cmd)); startPos = %scan(token: i_cmd: x); if ((startPos > 1) and (%subst(i_cmd: startPos - 1: 1) <> ' ')); x = startPos + %len(token); iter; else; leave; endif; enddo;
// Wenn das Token gefunden werden konnte, ... if (startPos <> 0); // ... dann // ermitteln Lnge des Schlsselwortes ... x = startPos + %len(token); numBracket = 1; dataLen = %len(token); cmdDtaLen = %len(i_cmd);
for y = x to cmdDtaLen;
DataLen = DataLen + 1;
// ... '(' if (%subst(i_cmd: y: 1) = '('); numBracket = numBracket + 1; endif;
// ... ')' if (%subst(i_cmd: y: 1) = ')'); numBracket = numBracket - 1;
// ... gefunden if (numBracket <= 0); leave; else; iter; endif; endif;
endfor;
// Wenn der Klammernzhler <> 0 ist, ... if (numBracket <> 0); // ... dann // konnte das Ende des Schlsselwortes // nicht gefunden werden clear PRZ0006; PRZ00061 = %subst(token: 1: %len(token) - 1); sendMsg(f_newMsg('PRZ0006': PRZ0006: cTYPE_DIAG)); endAbnormal(); endif;
// austauschen Schlsselwort cmd = %replace(i_keyData: i_cmd: startPos: dataLen);
else; // ... sonst // anfgen Schlsselwort cmd = %trim(i_cmd) + ' ' + i_keyData; endif;
return cmd;
/end-free P e * * ============================================================= * Ermitteln Lnge von srcDta * ============================================================= P getSrcDataLength... P b D pi like(int4_t) * * Funktionswert D srcDtaLen s like(int4_t) inz /free
srcDtaLen = f1.rcdLen - (%size(ds_srcFile.srcSeq) + %size(ds_srcFile.srcDat));
return srcDtaLen;
/end-free P E * * ============================================================= * Ermitteln der getStartPosition * ============================================================= P getStartPos... P b D pi like(int4_t) D i_tag value like(tag_t) D i_srcDta like(srcDta_t) * * Funktionswert D pos s like(int4_t) inz * * Arbeitsfelder /free
pos = %scan(%trim(i_tag): i_srcDta: 1); if (pos > 0); pos = pos + %len(%trim(i_tag)); pos = %check(' ': i_srcDta: pos); endif;
return pos;
/end-free P e * * ============================================================= * Prfen auf gltiges Tag * ============================================================= P isTag... P b D pi like(bool_t) D i_srcDta value like(srcDta_t) D o_tag like(tag_t) * * Funktionswert D isTag s like(bool_t) inz(cFalse) /free
isTag = cFalse;
dou '1'; // Anfang Pre-Compiler if (retrieveTag(cTAG_PREPRZ: i_srcDta: o_tag)); isTag = cTrue; leave; endif;
// Ende Pre-Compiler if (retrieveTag(cTAG_ENDPREPRZ: i_srcDta: o_tag)); isTag = cTrue; leave; endif;
// Anfang Link if (retrieveTag(cTAG_LINK: i_srcDta: o_tag)); isTag = cTrue; leave; endif;
// Ende Link if (retrieveTag(cTAG_ENDLINK: i_srcDta: o_tag)); isTag = cTrue; leave; endif;
// Anfang Compiler if (retrieveTag(cTAG_COMPILE: i_srcDta: o_tag)); isTag = cTrue; leave; endif;
// Ende Compiler if (retrieveTag(cTAG_ENDCOMPILE: i_srcDta: o_tag)); isTag = cTrue; leave; endif;
// Anfang Important if (retrieveTag(cTAG_IMPORTANT: i_srcDta: o_tag)); isTag = cTrue; leave; endif;
// Ende Important if (retrieveTag(cTAG_ENDIMPORTANT: i_srcDta: o_tag)); isTag = cTrue; leave; endif;
// Breite der Quellenangaben if (retrieveTag(cTAG_WIDTH: i_srcDta: o_tag)); isTag = cTrue; leave; endif;
// Erstellungsbefehl if (retrieveTag(cTAG_CRTCMD: i_srcDta: o_tag)); isTag = cTrue; leave; endif;
// Parameter if (retrieveTag(cTAG_PARM: i_srcDta: o_tag)); isTag = cTrue; leave; endif;
// Ausfhren "Hauptbefehl" if (retrieveTag(cTAG_EXECUTE: i_srcDta: o_tag)); isTag = cTrue; leave; endif;
// Ausfhren Pre-/Post-Anweisung if (retrieveTag(cTAG_CMD: i_srcDta: o_tag)); isTag = cTrue; leave; endif;
// Ausfhren Anweisung zum Setzen einer Bibliotheksliste if (retrieveTag(cTAG_LIBL: i_srcDta: o_tag)); isTag = cTrue; leave; endif;
// Ausfhren Anweisung zum Setzen der aktuellen Bibliothek if (retrieveTag(cTAG_CURLIB: i_srcDta: o_tag)); isTag = cTrue; leave; endif; enddo;
return isTag;
/end-free P e * * ============================================================= * Ermitteln Tag aus Quellendaten * ============================================================= P retrieveTag... P b D pi like(bool_t) D i_tagToTest value like(tag_t) D i_srcDta value like(srcDta_t) D o_tag like(tag_t) * * Funktionswert D isTag s like(bool_t) inz(cFalse) * * Arbeitsfelder D startPos s like(int4_t) inz * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /free
startPos = %scan(%trim(i_tagToTest): i_srcDta); if (startPos > 0); o_tag = i_tagToTest; isTag = cTrue; else; o_tag = ''; isTag = cFalse; endif;
return isTag;
/end-free P e * * ============================================================= * Umsetzen Tag in Betriebsmodus * ============================================================= P tagToMode... P b D pi like(mode_t) D i_tag value like(tag_t) * * Funktionswert D mode s like(mode_t) inz * * Nachrichten * ... Das Ende des Schlsselwortes &1 konnte nicht gefunden werden. /COPY QSTRPREPRC,PRZ0010 /free
select; // Anfang Pre-Compiler when (i_tag = cTAG_PREPRZ); mode = cMODE_PREPRZ;
// Ende Pre-Compiler when (i_tag = cTAG_ENDPREPRZ); mode = cMODE_ENDPREPRZ;
// Anfang Link when (i_tag = cTAG_LINK); mode = cMODE_LINK;
// Ende Link when (i_tag = cTAG_ENDLINK); mode = cMODE_ENDLINK;
// Anfang Compile when (i_tag = cTAG_COMPILE); mode = cMODE_COMPILE;
// Ende Compile when (i_tag = cTAG_ENDCOMPILE); mode = cMODE_ENDCOMPILE;
// Anfang Important when (i_tag = cTAG_IMPORTANT); mode = cMODE_IMPORTANT;
// Ende Important when (i_tag = cTAG_ENDIMPORTANT); mode = cMODE_ENDIMPORTANT;
// Maximale Breite der Quellendaten when (i_tag = cTAG_WIDTH); mode = cMODE_WIDTH;
// Erstellungsbefehl when (i_tag = cTAG_CRTCMD); mode = cMODE_CRTCMD;
// Parameter when (i_tag = cTAG_PARM); mode = cMODE_PARM;
// Ausfhren "Hauptbefehl" when (i_tag = cTAG_EXECUTE); mode = cMODE_EXECUTE;
// Ausfhren Pre-/Post-Befehle when (i_tag = cTAG_CMD); mode = cMODE_CMD;
// Ausfhren Anweisung zum Setzen einer Bibliotheksliste when (i_tag = cTAG_LIBL); mode = cMODE_LIBL;
// Ausfhren Anweisung zum Setzen der aktuellen Bibliothek when (i_tag = cTAG_CURLIB); mode = cMODE_CURLIB;
other; // ... darf nicht vorkommen, da alle Tags, die erkannt werden // auch umgesetzt werden mssen. // Dennoch: clear PRZ0010; PRZ00101 = i_tag; sendMsg(f_newMsg('PRZ0010': PRZ0010: cTYPE_DIAG)); endAbnormal(); endsl;
return mode;
/end-free P e * * ============================================================= * ndern Betriebsmodus * ============================================================= P checkMode... P b D pi n D i_mode value like(mode_t) D i_newMode value like(mode_t) * * Nachrichten * ... Falsche Reihenfolge im Betriebsmodus. /COPY QSTRPREPRC,PRZ0001 /free
P1KEY2.MODE = i_mode; P1KEY2.NEWMODE = i_newMode; setll %kds(P1Key2) FSTRPRE1;
dow '1'; reade %kds(P1Key2) FSTRPRE1; if (%eof()); clear PRZ0001; PRZ00011 = i_mode; PRZ00012 = i_newMode; sendMsg(f_newMsg('PRZ0001': PRZ0001: cTYPE_DIAG)); return cFalse; else; leave; endif; enddo;
return cTrue;
/end-free P e * * ============================================================= * Senden Escape Nachricht * ============================================================= P sendMsg... P b D pi D i_msg value likeds(msg_t) /free
f_sndPgmMsg(i_msg: cMsg_Prv: cMsg_ToCtlBdy);
/end-free P e * * ============================================================= * Mode auf Stack * ============================================================= P pushMode... P b * D pushMode... D pi D i_mode value like(mode_t) /free
g_modeCnt = g_modeCnt + 1; g_mode(g_modeCnt) = i_mode;
/end-free P e * * ============================================================= * Mode vom Stack * ============================================================= P popMode... P b * D popMode... D pi like(mode_t) /free
g_mode(g_modeCnt) = ''; g_modeCnt = g_modeCnt - 1;
return g_mode(g_modeCnt);
/end-free P e * * ============================================================= * Prfen CL Befehl * ============================================================= P validateCmd... P b D pi like(bool_t) D i_cmd value like(cmdLong_t) * * Definitionen fr QCAPCMD D optCtrl ds likeds(cpop0100_t) inz D retCmd s like(cmdLongFixLen_t) inz D retCmdLenAvl s 10i 0 inz(%len(retCmd)) D retCmdLenRtn s 10i 0 inz D errCode ds likeds(errCode_t) inz * * ... Fehler im Erstellungsbefehl gefunden. /COPY QSTRPREPRC,PRZ0015 * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /free
clear errCode;
monitor;
clear optCtrl; optCtrl.typeCmdPrc = cQCAPCMD_PRC_CHK_CMDLINE; optCtrl.DBCS_data = cQCAPCMD_DBCS_HANDLE; optCtrl.prmtAction = cQCAPCMD_PROMPT_NEVER; optCtrl.cmdStrSyntax = cQCAPCMD_SYNTAX_SYSTEM; optCtrl.msgRtvKey = ''; optCtrl.ccsidCmdStr = 0; optCtrl.res_01 = *ALLx'00';
errCode = f_newApiErrCode(cTrue); QCAPCMD(i_cmd: %len(%trimR(i_cmd)): optCtrl: %size(optCtrl) : 'CPOP0100': retCmd: retCmdLenAvl: retCmdLenRtn: errCode);
if (not f_isApiError(errCode)); return cTrue; endif;
on-error; endmon;
appendToJobLog(i_cmd);
return cFalse;
/end-free P e * * ============================================================= * Execute a CL command. * ============================================================= P executeCmd... P b D pi like(bool_t) D i_monErr value like(bool_t) D i_cmd value like(cmdLong_t) * * Fields for getMsgInf() D msgID s like(msgID_t) inz D msgDta s like(msg_t.data) inz D msgFile s like(msg_t.file) inz D msgLib s like(msg_t.lib) inz * * Definitionen fr Ausgangsprogramm D cmdLongFixLength... D s like(cmdLongFixLen_t) D cmdLongLength s 15P 5 D exitPgmName s 21A varying * D exitPgm pr extpgm(exitPgmName) D cmd like(cmdLongFixLen_t) D options(*varsize) D length 15P 5 options(*nopass: *omit) * * Definitionen fr QCAPCMD D optCtrl ds likeds(cpop0100_t) inz D retCmd s like(cmdLongFixLen_t) inz D retCmdLenAvl s 10i 0 inz(%len(retCmd)) D retCmdLenRtn s 10i 0 inz D errCode ds likeds(errCode_t) inz * * ... Fehler im Erstellungsbefehl gefunden. /COPY QSTRPREPRC,PRZ0015 * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /free
clear errCode;
if (g_pgmCmdExc <> '*NONE'); exitPgmName = %trim(g_libPgmCmdExc) + '/' + %trim(g_pgmCmdExc); cmdLongFixLength = i_cmd; cmdLongLength = %len(cmdLongFixLength); exitPgm(cmdLongFixLength: cmdLongLength); i_cmd = %trimR(%subst(cmdLongFixLength: 1: %int(cmdLongLength))); endif;
// Execute command monitor;
clear optCtrl; optCtrl.typeCmdPrc = cQCAPCMD_PRC_EXC_CMDLINE; optCtrl.DBCS_data = cQCAPCMD_DBCS_HANDLE; optCtrl.prmtAction = cQCAPCMD_PROMPT_NEVER; optCtrl.cmdStrSyntax = cQCAPCMD_SYNTAX_SYSTEM; optCtrl.msgRtvKey = ''; optCtrl.res_01 = *ALLx'00';
errCode = f_newApiErrCode(cFalse); QCAPCMD(i_cmd: %len(i_cmd): optCtrl: %size(optCtrl): 'CPOP0100' : retCmd: retCmdLenAvl: retCmdLenRtn: errCode);
return cTrue;
on-error;
appendToJobLog(i_cmd);
if (not g_option.isEventf); clear PRZ0015; PRZ00151 = i_cmd; getMsgInf('STRPREPRC': 'STRPREPRC2': 'executeCmd' : errCode.excID: msgID: msgDta: msgFile: msgLib); sendMsg(f_newMsg('PRZ0015': PRZ0015: cTYPE_DIAG)); sendMsg(f_newMsg(msgID: msgDta: cTYPE_DIAG: msgFile: msgLib)); endif;
if (i_monErr); return cFalse; else; endAbnormal(); endif; endmon;
/end-free P e * *===============================================================* * Appends a message to the job log. *===============================================================* P appendToJobLog... P b D pi D i_message value like(cmdLong_t) * D startPos S 10U 0 inz(1) D prefix S 11A inz D line S 60A inz * * Nachrichten * ... &1. /COPY QSTRPREPRC,PRZ0029 /free
if (i_message = ''); i_message = '*MISSING'; endif;
clear PRZ0029; prefix = 'STRPREPRC: '; dow (startPos > 0); line = f_wordWrap(i_message: %len(line): startPos); clear PRZ0029; PRZ00291 = prefix + line; sendMsg(f_newMsg('PRZ0029': PRZ0029: cTYPE_DIAG)); prefix = ''; enddo;
/end-free P E * * ============================================================= * Ermitteln Nachrichteninformationen * ============================================================= P getMsgInf... P b D pi D i_pgm value like(pgm_t) D i_module value like(module_t) D i_proc value like(proc_t) D i_msgId value like(msgID_t) D o_msgID like(msgID_t) D o_msgDta like(msg_t.data) D o_msgFile like(msg_t.file) D o_msgLib like(msg_t.lib) * D RCVM0200 ds likeds(rcvm0200_t) * D lenMsgInf s 10i 0 inz(%size(RCVM0200)) D stackEntry s 64a inz D stackCounter s 10i 0 inz(0) D msgType s 10a inz D msgKey s 4a inz D wait s 10i 0 inz(0) D msgAction s 10a inz('*SAME') D lenStackEntry s 10i 0 inz(%size(stackEntry)) D stackQual ds D stackQualMod 10a inz('*NONE') D stackQualPgm 10a inz('*NONE') D stackDataType s 10a inz('*CHAR') D ccsid s 10i 0 inz(0) * D errCode ds likeds(errCode_t) inz * D firstMsg s like(RCVM0200) inz * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /free
stackEntry = i_proc; stackQualPgm = i_pgm; stackQualMod = i_module; msgType = '*LAST'; msgKey = ''; firstMsg = '';
dou (rcvm0200.msgId = i_msgId);
clear RCVM0200; errCode = f_newApiErrCode(cTrue);
QMHRCVPM(RCVM0200: lenMsgInf: 'RCVM0200': stackEntry: stackCounter : msgType: msgKey: wait: msgAction: errCode : lenStackEntry: stackQual);
if (f_isApiError(errCode)); leave; endif;
if (rcvm0200.bytAvl = 0); RCVM0200 = firstMsg; leave; endif;
if (firstMsg = ''); firstMsg = RCVM0200; endif;
msgType = '*PRV'; msgKey = rcvm0200.msgKey; enddo;
if (f_isApiError(errCode)); o_msgID = ''; o_msgDta = ''; o_msgFile = ''; o_msgLib = ''; else; o_msgID = rcvm0200.msgId; o_msgDta = %subst(rcvm0200.varLenData: 1: rcvm0200.lenRplDtaRet); o_msgFile = rcvm0200.msgF; o_msgLib = rcvm0200.msgFLibRet; endif;
/end-free P e * * ============================================================= * Formatieren CL Befehl * ============================================================= P formatCmd... P b D pi like(cmdLong_t) D i_cmd value like(cmdLong_t) * * Nachrichten * ... Befehlszeichenfolge ist zu lang. /COPY QSTRPREPRC,PRZ0007 * ... Fehler im Erstellungsbefehl gefunden. /COPY QSTRPREPRC,PRZ0015 * * Definitionen fr QCAPCMD D cmd s like(i_cmd) D optCtrl ds likeds(cpop0100_t) inz D retCmd s like(cmdLongFixLen_t) inz D retCmdLenAvl s 10i 0 inz(%len(retCmd)) D retCmdLenRtn s 10i 0 inz D errCode ds likeds(errCode_t) inz * D msg ds likeds(msg_t) inz * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /free
if (i_cmd = ''); return i_cmd; endif;
cmd = i_cmd;
clear optCtrl; optCtrl.typeCmdPrc = cQCAPCMD_PRC_CHK_CMDLINE; optCtrl.DBCS_data = cQCAPCMD_DBCS_HANDLE; optCtrl.prmtAction = cQCAPCMD_PROMPT_NEVER; optCtrl.cmdStrSyntax = cQCAPCMD_SYNTAX_SYSTEM; optCtrl.msgRtvKey = ''; optCtrl.res_01 = *ALLx'00';
errCode = f_newApiErrCode(cTrue);
QCAPCMD(cmd: %len(cmd): optCtrl: %size(optCtrl): 'CPOP0100' : retCmd: retCmdLenAvl: retCmdLenRtn: errCode);
// Prfen API-Fehler ... if (f_isApiError(errCode)); msg = f_cvtApiErrCodeToMsg(errCode); msg.type = cTYPE_DIAG; sendMsg(msg); clear PRZ0015; PRZ00151 = i_cmd; sendMsg(f_newMsg('PRZ0015': PRZ0015: cTYPE_DIAG)); endAbnormal(); endif;
// Prfen Lnge der Befehlszeichenkette ... if (retCmdLenRtn > retCmdLenAvl); clear PRZ0007; PRZ00071 = retCmdLenAvl; sendMsg(f_newMsg('PRZ0007': PRZ0007: cTYPE_DIAG)); endAbnormal(); endif;
return retCmd;
/end-free P e * * ============================================================= * Beenden Programm abnormal * ============================================================= P endAbnormal... P b D pi D i_qEventf const likeds(qObj_t) D options(*nopass) * D p_qEventf c 1 * D qEventf ds likeds(i_qEventf) * * Nachrichten * ... Verarbeitung in Zeile &1 abgebrochen. /COPY QSTRPREPRC,PRZ0002 /free
if (%parms() >= p_qEventf and %addr(i_qEventf) <> *null); qEventf = i_qEventf; else; clear qEventf; endif;
if (g_option.isEventf); writeJobLogToEventf(qEventf: cFalse); endif;
if (%open(SRCFILE)); close SRCFILE; endif;
if (%open(STRPRE1)); close STRPRE1; endif;
clear PRZ0002; PRZ00021 = f1_line_number; sendMsg(f_newMsg('PRZ0002': PRZ0002: cTYPE_ESCAPE));
/end-free P e * * ============================================================= * Beenden Programm normal * ============================================================= P endOK... P b D pi D i_qEventf const likeds(qObj_t) D options(*nopass) * D p_qEventf c 1 * D qEventf ds likeds(i_qEventf) * * Nachrichten * ... EVFEVENT Datei in Bibliothek &1 oder das Job Protokoll konnte nicht geffnet /COPY QSTRPREPRC,PRZ0025 /free
if (%parms() >= p_qEventf and %addr(i_qEventf) <> *null); qEventf = i_qEventf; else; clear qEventf; endif;
if (g_option.isEventf); writeJobLogToEventf(qEventf: cTrue); endif;
/end-free P e * * ============================================================= * Write job log entries to the event file. * * i_qEventf - Event file created by the object creation * command. If the object name was overridden by * a STRPREPRC >>PARM<< tag, the events ctreated by * the object creation command must be copied to * the event file created by STRPREPRC. * ============================================================= P writeJobLogToEventf... P b D pi D i_qEventf const likeds(qObj_t) D i_chgMsgs n const * D isEmpty s n * ... EVFEVENT Datei in Bibliothek &1 oder das Job Protokoll konnte nicht geffnet /COPY QSTRPREPRC,PRZ0025 /free
monitor;
// Event file of IBM object creation command if (i_qEventf.lib <> '' and i_qEventf.name <> ''); isEmpty = isEmptyEventFile(i_qEventf.lib: i_qEventf.name); else; isEmpty = cTrue; endif;
g_eventf.isOpen = openEventFile(g_eventf.mbr: g_eventf.lib: i_qEventf);
if (g_eventf.isOpen);
if (isEmpty or g_option.doAppendJobLog); if (i_chgMsgs); writeEventFile( g_srcFile.name: g_srcFile.lib: g_srcFile.mbr : f1_line_number : EVF_MSG_SEVERITY_ERROR: EVF_MSG_SEVERITY_WARNING); else; writeEventFile( g_srcFile.name: g_srcFile.lib: g_srcFile.mbr : f1_line_number); endif; endif;
endif;
on-error; g_eventf.isOpen = cFalse; clear PRZ0025; PRZ00251 = g_eventf.lib; sendMsg(f_newMsg('PRZ0025': PRZ0025: cTYPE_DIAG)); endmon;
// Allways try to close the event file.
monitor;
if (g_eventf.isOpen); closeEventFile(); g_eventf.isOpen = cFalse; endif;
on-error; // Ignore close errors. endmon;
/end-free P e * *===============================================================* * Replace variables *===============================================================* P replaceVar... P b D pi D i_var 10a value D i_value value like(cmdLong_t) D io_string like(cmdLong_t) * D x s like(int4_t) /free
dou (x = 0); if (x > 0); io_string = %replace(%trim(i_value) : io_string : x : %len(%trim(i_var))); endif; x = %scan(%trim(i_var): io_string); enddo;
/end-free P E * ** CTDATA g_crtCmdsDta CRTBNDCL PGM Create Bound CL Program CRTBNDRPG PGM Create Bound RPG Program CRTCBLMOD MODULE Create COBOL Module CRTCLMOD MODULE Create CL Module CRTCLD CLD Create C Locale Description CRTCMOD MODULE Create C Module CRTCMD CMD Create Command CRTCPPMOD MODULE Create C++ Module CRTDSPF FILE Create Display File CRTMNU MENU Create Menu CRTPNLGRP PNLGRP Create Panel Group CRTPRTF FILE Create Printer File CRTQMQRY QMQRY Create Query Management Query CRTRPGMOD MODULE Create RPG Module CRTSQLCBL PGM Create SQL COBOL Program CRTSQLCBLI OBJ Create SQL ILE COBOL Object CRTSQLCI OBJ Create SQL ILE C object CRTSQLCPPI OBJ Create SQL ILE C++ Object CRTSQLRPG PGM Create SQL RPG Program CRTSQLRPGI OBJ Create SQL ILE RPG Object CRTTBL TBL Create Table XCRTSQLOBJ SQLOBJ Create SQL-Object

