open STRPRE1;
if (f1.numRcd <> %elem(ctrlEntry));
setll 1 FSTRPRE1; dow (%found(STRPRE1)); read FSTRPRE1; if (%eof(STRPRE1)); leave; endif; delete FSTRPRE1; enddo;
for x = 1 to %elem(ctrlEntry); MODE = %subst(ctrlEntry(x): 1: 30); NEWMODE = %subst(ctrlEntry(x): 31: 30); write FSTRPRE1; endfor; endif;
close STRPRE1;
open STRPRE2;
if (f2.numRcd = 0);
setll 1 FSTRPRE2; dow (%found(STRPRE2)); read FSTRPRE2; if (%eof(STRPRE2)); leave; endif; delete FSTRPRE2; enddo;
for x = 1 to %elem(msgIDs); MSGFILE = %subst(msgIDs(x): 1: 10); MSGLIB = %subst(msgIDs(x): 12: 10); MSGID = %subst(msgIDs(x): 23: 7); FMT = %dec(%subst(msgIDs(x): 31: 2): 2: 0); TEXT = %subst(msgIDs(x): 34: 50); write FSTRPRE2; endfor; endif;
close STRPRE2;
/end-free P E * ** CTDATA(ctrlEntry) *SCAN >>PRE-COMPILER<< >>COMPILE<< >>END-COMPILE<< >>COMPILE<< >>PARM<< >>IMPORTANT<< >>END-IMPORTANT<< >>IMPORTANT<< >>PARM<< >>LINK<< >>END-LINK<< >>LINK<< >>PARM<< >>PRE-COMPILER<< *EXIT >>PRE-COMPILER<< >>CMD<< >>PRE-COMPILER<< >>COMPILE<< >>PRE-COMPILER<< >>IMPORTANT<< >>PRE-COMPILER<< >>CRTCMD<< >>PRE-COMPILER<< >>EXECUTE<< >>PRE-COMPILER<< >>LINK<< >>PRE-COMPILER<< >>WIDTH<< >>PRE-COMPILER<< >>CURLIB<< >>PRE-COMPILER<< >>LIBL<< ** CTDATA(msgIDs) 12345678901234567890123456789012345678901234567890 QCPFMSG *LIBL CPD5CDB 04 The signature of an export block has been trun ... # 1 QCPFMSG *LIBL CPD5CDC 04 The signature of an export block has been padd ... # 2 QCPFMSG *LIBL CPD5CDD 04 If LVLCHK(*NO) is specified on the Start Progr ... # 3 QCPFMSG *LIBL CPD5CDE 04 A syntax error was detected in a signature spe ... # 4 QCPFMSG *LIBL CPD5CDF 04 Signature '&8' was specified on the Start Prog ... # 5 QCPFMSG *LIBL CPD5CD6 04 The wildcard specified, '&8', does not match a ... # 6 QCPFMSG *LIBL CPD5CD7 04 The wildcard specified, '&8', matches &10 symb ... # 7 QCPFMSG *LIBL CPD5CEA 04 An ENDPGMEXP command was expected to end an ex ... # 8 QCPFMSG *LIBL CPD5CEB 04 An export block was not started before the END ... # 9 QCPFMSG *LIBL CPD5CEC 04 An EXPORT binder language command exists outsi ... #10 QCPFMSG *LIBL CPD5CED 04 The current export block of the binder languag ... #11 QCPFMSG *LIBL CPD5CEE 04 No export block of the binder language source ... #12 QCPFMSG *LIBL CPD5CEF 04 The system has generated identical signatures ... #13 QCPFMSG *LIBL CPD5CE0 04 Symbol '&8' is specified more than once in a s ... #14 QCPFMSG *LIBL CPD5CE1 04 More than one export block is declared to be t ... #15 QCPFMSG *LIBL CPD5CE2 04 Two or more export blocks in the binder langua ... #16 QCPFMSG *LIBL CPD5CE3 04 The current export block exports fewer symbols ... #17 QCPFMSG *LIBL CPD5CE4 04 Level checking can be disabled only once in a ... #18 QCPFMSG *LIBL CPD5