    /* =================================================================== */                      
    /* >>PRE-COMPILER<<                                                    */                      
    /*                                                                     */                      
    /*   >>CRTCMD<< CRTCMD       CMD(&LI/STRPREPRC) +                      */                      
    /*                           SRCFILE(&SL/&SF) +                        */                      
    /*                           SRCMBR(&SM);                              */                      
    /*                                                                     */                      
    /*   >>COMPILE<<                                                       */                      
    /*     >>PARM<< PGM(*LIBL/STRPREPRC);                                  */                      
    /*     >>PARM<< HLPPNLGRP(*LIBL/STRPREPRCP);                           */                      
    /*     >>PARM<< HLPID(STRPREPRC);                                      */                      
    /*     >>PARM<< MSGF(*LIBL/STRPREPRC);                                 */                      
    /*     >>PARM<< PRDLIB(&LI);                                           */                      
    /*   >>END-COMPILE<<                                                   */                      
    /*                                                                     */                      
    /*   >>EXECUTE<<                                                       */                      
    /*                                                                     */                      
    /* >>END-PRE-COMPILER<<                                                */                      
    /* =================================================================== */                      
                                                                                                   
