werden. /COPY QSTRPREPRC,PRZ0026 * ... Erstellungsbefehl nicht gefunden in Quellendatei &2/&1, Teildatei &3. /COPY QSTRPREPRC,PRZ0028 * * Arbeitsfelder D i S 10i 0 inz D posOfVar S like(int4_t) inz D srcDta S like(ds_srcFile.srcDta) D tag S like(tag_t) inz D mode S like(mode_t) inz D newMode S like(mode_t) inz D srcDtaLen S like(int4_t) inz D continue S like(continue_t) inz D keyData S 2048a varying inz D cmdString S like(cmdLong_t) inz D execCmdImplizit... D S like(bool_t) inz(cTrue) D foundPrePrcTags... D S like(bool_t) inz(cFalse) D cmdChgObjD S like(cmdLong_t) D cmd S like(cmdLong_t) D isCmdMissingError... D S n inz(cFalse) /free
p_setAppMsgFile(i_msgFile: i_msgFileLib);
// Initialisieren globaler Variablen reset g_msgFile; reset g_mode; reset g_modeCnt; reset g_pgmCmdExc; reset g_libPgmCmdExc; reset g_option; reset g_msgKey; reset g_option; reset g_eventf; g_msgFile = i_msgFile; g_pgmCmdExc = i_pgmCmdExc; g_libPgmCmdExc = i_libPgmCmdExc;
if (%parms() >= 36 and i_option.numE > 0); for i = 1 to i_option.numE; select; when (i_option.option(i) = cEVENTF_YES); // select; // when (not f_startsWith('*': i_lib)); g_option.isEventf = cTrue; g_eventf.mbr = i_obj; g_eventf.lib = i_lib; // when (not f_startsWith('*': i_srcLib)); // g_option.isEventf = cTrue; // g_eventf.mbr = i_srcMbr; // g_eventf.lib = i_srcLib; // when (not f_startsWith('*': i_useSrcLib)); // g_option.isEventf = cTrue; // g_eventf.mbr = i_srcMbr; // g_eventf.lib = i_useSrcLib; // other; // g_option.isEventf = cFalse; // endsl; when (i_option.option(i) = cEVENTF_NO); g_option.isEventf = cFalse; endsl; endfor; endif;
g_srcFile.name = i_srcFile; g_srcFile.lib = i_srcLib; g_srcFile.mbr = i_srcMbr;
if (i_chgObjD = '*YES'); cmdChgObjD = 'CHGOBJDA + OBJ(' + %trim(i_lib) + '/' + %trim(i_obj) + ') + OBJTYPE(' + %trim(i_objType) + ') + SRCF(' + %trim(i_objSrcFile) + ') + SRCFLIB(' + %trim(i_objSrcLib) + ') + SRCMBR(' + %trim(i_objSrcMbr) + ') + SRCDATE(' + %trim('*SRCMBR') + ') + SRCTIME(' + %trim('*SRCMBR') + ')'; else; cmdChgObjD = ''; endif;
open(E) SRCFILE; if (%error()); clear PRZ0012; PRZ00121 = f1.file; PRZ00122 = f1.lib; PRZ00123 = f1.mbr; p_sndMsg(f_newMsg('PRZ0012': PRZ0012: cTYPE_DIAG)); p_endAbn(); endif;
open(E) STRPRE1; if (%error()); clear PRZ0013; PRZ00131 = f2.file; PRZ00132 = f2.lib; PRZ00133 = f2.mbr; p_sndMsg(f_newMsg('PRZ0013': PRZ0013: cTYPE_DIAG)); p_endAbn(); endif;
// Entfernen der "ffnen" Nachrichten p_rmvPgmMsg(cMsg_Rmv_All);
// Prfen der Datensatzlnge if (f1.rcdLen > cMaxRcdLen); clear PRZ0011; PRZ00111 = f1.rcdLen; PRZ00112 = cMaxRcdLen; PRZ00113 = f1.lib; PRZ00114 = f1.file; p_sndMsg(f_newMsg('PRZ0011': PRZ0011: cTYPE_DIAG)); p_endAbn(); endif;
// Formatieren Befehl cmd = f_fmtCmd(%trim(i_cmd));
// Initialisieren Modus mode = cMODE_SCAN; p_pushMode(mode);
// Initialisieren "Ausfhren Befehl implizit" // Der Erstellungsbefehl wird ohne Pre-Prozessor // Abschnitt ausgefhrt execCmdImplizit = cTrue;
// Initialisieren "STRPREPRC" Anweisungen gefunden foundPrePrcTags = cFalse;
// Berechnen Datenlnge srcDtaLen = f_srcDtaLen();
// Initialisieren Fortsetzungszeile exsr resetLine;
// Lesen der Quellenteildatei f1_line_number = 0;
dow '1'; read SRCFILE ds_srcFile; if (%eof()); leave; endif;
f1_line_number = f1_line_number + 1;
// Extrahieren Datenteil gem der Datensatzlnge srcDta = %subst(ds_srcFile.srcDta: 1: srcDtaLen);
// Wenn sich in der Programmzeile ein Tag befindet, ... if (f_isTag(srcDta: tag)); // ermitteln neue Betriebsart newMode = f_tagToMode(tag);
// prfen Betriebsartenwechsel if (not f_chkMode(mode: newMode)); p_endAbn(); endif;
// umschalten Betriebsartenwechsel mode = newMode;
// Betriebsart merken p_pushMode(mode); else; // ... Standardverarbeitung endif;
// Anfang der Pre-Prozessoranweisungen if (mode = cMODE_PREPRZ); foundPrePrcTags = cTrue; execCmdImplizit = cFalse; iter; endif;
// Ende der Pre-Prozessoranweisungen if (mode = cMODE_ENDPREPRZ); mode = f_popMode; mode = f_popMode; leave; endif;
// Ende der Link Parameter if (mode = cMODE_ENDLINK); mode = f_popMode; mode = f_popMode; iter; endif;
// Ende der Compile Parameter if (mode = cMODE_ENDCOMPILE); mode = f_popMode(); mode = f_popMode(); iter; endif;
// Ende der Important Parameter if (mode = cMODE_ENDIMPORTANT); mode = f_popMode(); mode = f_popMode(); iter; endif;
// Breite der Quellenangaben >>WIDTH<< if (mode = cMODE_WIDTH); srcDtaLen = f_getWidth(srcDta: srcDtaLen); mode = f_popMode(); endif;
// Erstellungsbefehl >>CRTCMD<< if (mode = cMODE_CRTCMD); // eintragen Erstellungsbefehl keyData = f_addData(cTAG_CRTCMD : keyData : srcDta : continue );
// prfen auf Fortsetzungszeile if (continue <> cEND_OF_LINE); iter; else; if (cmd = ''); cmd = keyData; endif; exsr resetLine; mode = f_popMode(); iter; endif; endif;
// Parm-Anweisung >>PARM<< if (mode = cMODE_PARM); if (cmd = ''); clear PRZ0014; p_sndMsg(f_newMsg('PRZ0014': PRZ0014: cTYPE_DIAG)); p_endAbn(); endif;
// zufgen Schlsselwortdaten keyData = f_addData(cTAG_PARM : keyData : srcDta : continue );
// prfen auf Fortsetzungszeile if (continue <> cEND_OF_LINE); iter; else; cmd = f_replaceCmdKeyWord(cmd: keyData); exsr resetLine; mode = f_popMode(); iter; endif; endif;
// Execute-Anweisung >>EXECUTE<< if (mode = cMODE_EXECUTE); cmdString = cmd; exsr replaceVar; p_performExecute(cmdString: cmdChgObjD); mode = f_popMode(); iter; endif;
// Befehl-Anweisung >>CMD<< if (mode = cMODE_CMD); // zufgen Schlsselwortdaten keyData = f_addData(cTAG_CMD : keyData : srcDta : continue );
// prfen auf Fortsetzungszeile if (continue <> cEND_OF_LINE); iter; else; cmdString = keyData; exsr replaceVar; p_performCmd(cmdString); exsr resetLine; mode = f_popMode(); iter; endif; endif;
enddo;
close SRCFILE; close STRPRE1;
// Ausfhren >>EXECUTE<< implizit, weil // kein Pre-Prozessor Abschnitt gefunden werden konnte isCmdMissingError = cFalse; if (execCmdImplizit = cTrue); if (cmd = ''); isCmdMissingError = cTrue; else; cmdString = cmd; exsr replaceVar; p_performExecute(cmdString: cmdChgObjD); endif; endif;
// Schlieen der EVFEVENT Datei if (g_eventf.isOpen); closeEventFile(); g_eventf.isOpen = cFalse; endif;
// Senden Abbruchnachricht, weil keine STRPREPRC Anweisungen // gefunden worden sind if (not foundPrePrcTags and i_tagsReq); clear PRZ0024; PRZ00241 = f1.file; PRZ00242 = f1.lib; PRZ00243 = f1.mbr; p_sndMsg(f_newMsg('PRZ0024': PRZ0024: cTYPE_ESCAPE)); endif;
if (isCmdMissingError); clear PRZ0028; PRZ00281 = f1.file; PRZ00282 = f1.lib; PRZ00283 = f1.mbr; p_sndMsg(f_newMsg('PRZ0028': PRZ0028: cTYPE_ESCAPE)); endif;
return;
// - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - // Zurcksetzen Werte fr neue Schlsselwortdaten // - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - begsr resetLine;
continue = cNEW_LINE; keyData = '';
endsr;
// - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - // Ersetzen der Ersatzvariablen // - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - begsr replaceVar;
// Ersetzen Lib (&LI) if (i_lib <> cNone); p_replaceVar('&LI': i_lib: cmdString); endif;
// Ersetzen Objekt (&OB) if (i_obj <> cNone); p_replaceVar('&OB': i_obj: cmdString); endif;
// Ersetzen Type Code (&TY) if (i_typeCode <> cNone); p_replaceVar('&TY': i_typeCode: cmdString); endif;
// Ersetzen Source Lib (&SL) if (i_srcLib <> cNone); p_replaceVar('&SL': i_srcLib: cmdString); endif;
// Ersetzen Source File (&SF) if (i_srcFile <> cNone); p_replaceVar('&SF': i_srcFile: cmdString); endif;
// Ersetzen Source Mbr (&SM) if (i_srcMbr <> cNone); p_replaceVar('&SM': i_srcMbr: cmdString); endif;
// Ersetzen Test Lib (&TL) if (i_testLib <> cNone); p_replaceVar('&TL': i_testLib: cmdString); endif;
// Ersetzen Test Object (&TO) if (i_testObj <> cNone); p_replaceVar('&TO': i_testObj: cmdString); endif;
// Ersetzen Taget Rel. (&TO) if (i_tgtRls <> cNone); p_replaceVar('&TR': i_tgtRls: cmdString); endif;
// Ersetzen From Src Lib (&SL) if (i_frmSrcLib <> cNone); p_replaceVar('&FL': i_frmSrcLib: cmdString); endif;
// Ersetzen From Src File (&SF) if (i_frmSrcFile <> cNone); p_replaceVar('&FF': i_frmSrcFile: cmdString); endif;
// Ersetzen From Src Mbr (&SM) if (i_frmSrcMbr <> cNone); p_replaceVar('&FM': i_frmSrcMbr: cmdString); endif;
// Ersetzen Parameter (&USER0) if (i_user0 <> cNone); p_replaceVar('&U0': i_user0: cmdString); endif;
// Ersetzen Parameter (&USER1) if (i_user1 <> cNone); p_replaceVar('&U1': i_user1: cmdString); endif;
// Ersetzen Parameter (&USER2) if (i_user2 <> cNone); p_replaceVar('&U2': i_user2: cmdString); endif;
// Ersetzen Parameter (&USER3) if (i_user3 <> cNone); p_replaceVar('&U3': i_user3: cmdString); endif;
// Ersetzen Parameter (&USER4) if (i_user4 <> cNone); p_replaceVar('&U4': i_user4: cmdString); endif;
// Ersetzen Parameter (&USER5) if (i_user5 <> cNone); p_replaceVar('&U5': i_user5: cmdString); endif;
// Ersetzen Parameter (&USER6) if (i_user6 <> cNone); p_replaceVar('&U6': i_user6: cmdString); endif;
