D jobLogEntry ds likeds(JobLog_jobLogEntry_t) inz /free
if (JobLog_getNewest('*': jobLogEntry)); g_status.haveJobLog = cTrue; g_status.msgKey = jobLogEntry.msgKey; else; g_status.haveJobLog = cFalse; g_status.msgKey = *ALLx'FF'; endif;
/end-free P e * * ============================================================= * Opens the event file. * ============================================================= P openEventFile... P b export D pi n D i_obj 10a const D i_lib 10a const
D msg ds likeds(msg_t) D rtnLib s like(i_lib) /free
Eventf_open(i_lib: i_obj: rtnLib); g_status.haveEventFile = cTrue;
Eventf_writeProcessor();
monitor; in g_LDA; if (rtnLib <> ''); g_LDA.library = rtnLib; g_LDA.object = i_obj; else; g_LDA.library = ''; g_LDA.object = ''; endif; out g_LDA; on-error; endmon;
if (rtnLib <> ''); return cTrue; endif;
return cFalse;
/end-free P e * * ============================================================= * Copies the job log messages to the event file. * ============================================================= P writeEventFile... P b export D pi D i_srcFile 10a const D i_srcLib 10a const D i_srcMbr 10a const D i_lineNumber 10i 0 const D i_threshold 10i 0 const options(*nopass) D i_ovrMsgSev 10i 0 const options(*nopass)
D p_ovrMsgSev c 6
D threshold s like(i_threshold) D ovrMsgSev s like(i_ovrMsgSev) D path s like(evf_path_t) D numLines s 10i 0 inz D SOURCE_ID c 1
D hJobLog s like(JobLog_handle_t) inz D jobLogEntry ds likeds(JobLog_jobLogEntry_t) inz D isFirstMsg s n inz(cTrue) D lineNumber s like(i_lineNumber) /free
if (not g_status.haveEventFile); return; endif;
if (not g_status.haveJobLog); return; endif;
if (%parms() >= p_ovrMsgSev); ovrMsgSev = i_ovrMsgSev; threshold = i_threshold; else; ovrMsgSev = -1; threshold = -1; endif;
path = f_cvtQSYSObjNameToPath(i_srcFile: i_srcLib: i_srcMbr: '*FILE'); Eventf_writeFileID(SOURCE_ID: path: EVF_NAME_TYPE_PATH);
hJobLog = JobLog_open(*omit: JOBLOG_OPTION_MSG + JOBLOG_OPTION_REPLACE_VARS + JOBLOG_OPTION_SENDER : JOBLOG_NEXT : g_status.msgKey);
dow (JobLog_getEntry(hJobLog: jobLogEntry));
if (isFirstMsg); isFirstMsg = cFalse; iter; endif;
if (isIgnoredMsgID(jobLogEntry)); iter; endif;
lineNumber = getLineNumber(jobLogEntry); if (lineNumber = -1); lineNumber = i_lineNumber; endif;
if (ovrMsgSev <> -1 and jobLogEntry.msgSeverity >= threshold); jobLogEntry.msgSeverity = ovrMsgSev; endif;
Eventf_writeError(SOURCE_ID : lineNumber : jobLogEntry.msgID : jobLogEntry.msgSeverity : jobLogEntry.msgText); enddo;
JobLog_close(hJobLog);
Eventf_writeFileEnd(SOURCE_ID: numLines);
